import { extendStyle, overwriteStyle } from './component-style'
import { FieldProps, getFieldBaseStyle } from './field-base'
import { styled } from './styled'
import { defaultConfig } from './utils/config'

const Input = styled.input.withConfig(defaultConfig) <FieldProps>`
  ${extendStyle('Input')};

  ${({ theme, colorScheme }) => getFieldBaseStyle(theme, colorScheme)};

  ${overwriteStyle('Input')};
`

export default Input
