import Box, { Props } from './Box'
import { extendStyle, overwriteStyle } from './component-style'
import forwardRefAs from './forward-ref-as'
import { styled } from './styled'

const LinkBoxStyled = styled(Box)`
  color: inherit;
  display: block;
  text-decoration: none;
  background-color: transparent;
  border: none;
  cursor: pointer;

  &[disabled] {
    pointer-events: none;
    opacity: 0.7;
  }

  ${extendStyle('LinkBox')};
  ${overwriteStyle('LinkBox')};
`

const LinkBox = forwardRefAs<Props, 'a'>(function LinkBox({ as = 'a', ...props }, ref) {
  return <LinkBoxStyled ref={ref} {...props} as={as} />
})

export default LinkBox
