import { extendStyle, overwriteStyle } from './component-style'
import { FieldProps as Props, getFieldBaseStyle } from './field-base'
import { styled } from './styled'
import { defaultConfig } from './utils/config'

const Textarea = styled.textarea.withConfig(defaultConfig) <Props>`
  ${extendStyle('Textarea')};

  ${({ theme, colorScheme }) => getFieldBaseStyle(theme, colorScheme)};

  height: auto;

  ${overwriteStyle('Textarea')};
`

export default Textarea
