import { OneOfBreakpoints, Theme } from '../theme-types'

export function up(name: OneOfBreakpoints) {
  return ({ theme }: { theme: Theme }) => `@media (min-width:${theme.breakpoint[name]}px)`
}

export function down(name: OneOfBreakpoints) {
  return ({ theme }: { theme: Theme }) => `@media (max-width:${theme.breakpoint[name]}px)`
}

