import { alias, sx, SxCssAlias } from '../sx'
import { WithTheme } from '../theme-types'

export type SxAliasProps = {
  [K in keyof SxCssAlias]?: SxCssAlias[K] | Array<SxCssAlias[K]>
}

export function handleSxAliasProps<Props>({ theme, ...props }: WithTheme<Props>) {
  const aliasProps = Object.entries(props).reduce(
    (acc, [key, value]) => (alias[key as keyof typeof alias] ? { ...acc, [key]: value } : acc),
    {},
  )

  return sx(theme, aliasProps)
}

