import { AllColorsType, OneOfColorSchemes, OneOfPalettes } from '../theme-types'

const colorSchemeKeys: OneOfColorSchemes[] =
  ['primary', 'secondary', 'tertiary', 'success', 'warning', 'danger', 'light', 'inverse', 'highlight']

const paletteKeys: OneOfPalettes[] = ['blue', 'cyan', 'gray', 'indigo', 'moss', 'orange', 'pink', 'purple', 'red', 'teal', 'yellow']

export function colorIsColorScheme(color: AllColorsType): color is OneOfColorSchemes {
  return colorSchemeKeys.includes(color as OneOfColorSchemes)
}

export function colorIsPalette(color: AllColorsType): color is OneOfPalettes {
  return paletteKeys.includes(color as OneOfPalettes)
}


