import defautTheme from '../src/default-theme'

test('spacing', () => {
  expect(defautTheme.spacing(0)).toBe('0rem')
  expect(defautTheme.spacing(1)).toBe('0.125rem')

  expect(defautTheme.spacing(2)).toBe('0.25rem')
  expect(defautTheme.spacing(3)).toBe('0.5rem')
  expect(defautTheme.spacing(4)).toBe('0.75rem')
})
