import defaultTheme from '../src/default-theme'
import extendTheme from '../src/extend-theme'

test('should extendTheme return default defaultTheme merged with values received', () => {
  const value = { component: { Text: { base: { color: 'blue' } } } }
  const result = extendTheme(value)

  expect(result).toEqual({ ...value, ...defaultTheme })
})

test('should extendTheme return default defaultTheme merged with nested values received', () => {
  const value = { font: { family: { foo: 'bar' } } }
  const result = extendTheme(value)

  expect(result).toEqual({
    ...defaultTheme,
    font: {
      ...defaultTheme.font,
      family: { ...defaultTheme.font.family, ...value.font.family },
    },
  })
})

test('should extendTheme return default defaultTheme replaced by values received', () => {
  const value = { font: { weight: { bold: 1000 } } }
  const result = extendTheme(value)

  expect(defaultTheme.font.weight.bold).not.toBeFalsy()

  expect(result.font.weight.bold).toBe(value.font.weight.bold)
  expect(result).toEqual({
    ...defaultTheme,
    font: {
      ...defaultTheme.font,
      weight: { ...defaultTheme.font.weight, ...value.font.weight },
    },
  })
})
