import theme from '../../src/default-theme'
import { down, up } from '../../src/utils/media-queries'

test('should return a css media query with min-width based on breakpoints from theme', () => {
  const { xs, sm, md, lg, xl } = theme.breakpoint

  expect(up('xs')({ theme })).toBe(`@media (min-width:${xs}px)`)
  expect(up('sm')({ theme })).toBe(`@media (min-width:${sm}px)`)
  expect(up('md')({ theme })).toBe(`@media (min-width:${md}px)`)
  expect(up('lg')({ theme })).toBe(`@media (min-width:${lg}px)`)
  expect(up('xl')({ theme })).toBe(`@media (min-width:${xl}px)`)
})

test('should return a css media query with max-width based on breakpoints from theme', () => {
  const { xs, sm, md, lg, xl } = theme.breakpoint

  expect(down('xs')({ theme })).toBe(`@media (max-width:${xs}px)`)
  expect(down('sm')({ theme })).toBe(`@media (max-width:${sm}px)`)
  expect(down('md')({ theme })).toBe(`@media (max-width:${md}px)`)
  expect(down('lg')({ theme })).toBe(`@media (max-width:${lg}px)`)
  expect(down('xl')({ theme })).toBe(`@media (max-width:${xl}px)`)
})
