import theme from '../../src/default-theme'
import { handleSxAliasProps } from '../../src/utils/sx-props'

test('handleSxAliasProps()', () => {
  const defaultBreakNotDefined = {
    '@media (max-width:992px)': {
      padding: {},
    },
    padding: '0.125rem',
  }

  const defaultBreakDefined = {
    '@media (max-width:992px)': { width: '100%' },
    width: '50%',
  }

  expect(handleSxAliasProps({ theme })).toEqual({})
  expect(handleSxAliasProps({ theme, p: 1 })).toEqual({ padding: theme.spacing(1) })

  expect(handleSxAliasProps({ theme, p: [1] })).toEqual(defaultBreakNotDefined)
  expect(handleSxAliasProps({ theme, w: [6] })).toEqual(defaultBreakDefined)

  expect(handleSxAliasProps({ theme, bg: 'primary' })).toEqual({
    backgroundColor: theme.colorScheme.primary[500],
  })
})
