import { extendStyle, overwriteStyle, SxProp } from '@citric/core/dist/component-style'
import { AsProp } from '@citric/core/dist/forward-ref-as'
import { styled } from '@citric/core/dist/styled'
import { OneOfColorSchemes } from '@citric/core/dist/theme-types'
import { defaultConfig } from '@citric/core/dist/utils/config'
import { getColor, getSpacing } from '@citric/core/dist/utils/theme'

export interface AlertProps {
  sx?: SxProp,
  as?: AsProp,
  colorScheme?: OneOfColorSchemes,
}

const Alert = styled.div.withConfig(defaultConfig) <AlertProps>`
  width: 100%;
  padding: ${({ theme }) => `${getSpacing(theme, 2)} ${getSpacing(theme, 5)}`};
  box-sizing: border-box;
  display: flex;
  align-items: center;
  justify-content: space-between;

  ${extendStyle('Alert')};

  background-color: ${({ theme, colorScheme }) => getColor(theme, colorScheme || 'primary')};

  ${overwriteStyle('Alert')};
`

export default Alert
