import { extendStyle, overwriteStyle, SxProp } from '@citric/core/dist/component-style'
import { styled } from '@citric/core/dist/styled'
import { defaultConfig } from '@citric/core/dist/utils/config'
import { getColor, getFontStyle, getSpacing } from '@citric/core/dist/utils/theme'

export interface FieldAddonProps {
  sx?: SxProp,
}

const FieldAddon = styled.div.withConfig(defaultConfig) <FieldAddonProps>`
  height: 2.5rem;
  display: flex;
  align-items: center;
  padding-inline: ${({ theme }) => `${getSpacing(theme, 3)}`};
  box-sizing: border-box;

  ${extendStyle('FieldAddon')};

  border: 1px solid ${({ theme }) => getColor(theme, 'light.600')};
  background: ${({ theme }) => getColor(theme, 'light.400')};
  font: ${({ theme }) => getFontStyle(theme, 'body2')};
  color: ${({ theme }) => getColor(theme, 'light.contrastText')};

  ${overwriteStyle('FieldAddon')};
`

export default FieldAddon
