import { extendStyle, overwriteStyle } from '@citric/core/dist/component-style'
import { styled } from '@citric/core/dist/styled'
import ProgressLinear, { ProgressLinearProps } from './ProgressLinear'

export type LoadingLinearProps = Omit<ProgressLinearProps, 'percentage'>

const LoadingLinear = styled(ProgressLinear) <LoadingLinearProps>`
  &::after {
    animation: loopLinear 1s infinite;

    @keyframes loopLinear {
      0% {
        left: 0%;
        width: 5px;
      }
      50% {
        left: 30%;
        width: 40%;
      }
      100% {
        left: 99.3%;
        width: 5px;
      }
    }
  }

  ${extendStyle('LoadingLinear')}
  ${overwriteStyle('LoadingLinear')}
`

export default LoadingLinear
