import { extendStyle, overwriteStyle, SxProp } from '@citric/core/dist/component-style'
import { defaultConfig } from '@citric/core/dist/utils/config'
import { handleSxAliasProps, SxAliasProps } from '@citric/core/dist/utils/sx-props'
import { getColor, getRadius, getSpacing } from '@citric/core/dist/utils/theme'
import { styled } from 'styled-components'

export interface Props extends SxAliasProps {
  sx?: SxProp,
}

const Paper = styled.div.withConfig(defaultConfig) <Props>`
  padding: ${({ theme }) => `${getSpacing(theme, 7)}`};
  box-sizing: border-box;
  background-color: ${({ theme }) => getColor(theme, 'light.300')};
  border: 1px solid ${({ theme }) => getColor(theme, 'light')};
  border-radius: ${({ theme }) => getRadius(theme, 'sm')};

  ${handleSxAliasProps};
  ${extendStyle('Paper')};
  ${overwriteStyle('Paper')};
`
export default Paper
