import { defaultTheme } from '@citric/core'
import { OneOfColorSchemes } from '@citric/core/dist/theme-types'
import 'jest-styled-components'
import { createRef } from 'react'
import ProgressLinear from '../src/ProgressLinear'
import { buildThemeWithComponents, render } from './react'

const colorSchemes = Object.keys(defaultTheme.colorScheme) as OneOfColorSchemes[]

test('should render a ProgressLinear component', () => {
  const { container } = render(<ProgressLinear />)

  expect(container.firstChild).toMatchInlineSnapshot(`
    .c0 {
      position: relative;
      border-radius: 2rem;
      width: 100%;
      height: 0.25rem;
      background: #2F2F3C;
    }

    .c0::after {
      content: '';
      position: absolute;
      width: 50%;
      height: 0.125rem;
      top: 1px;
      left: 1px;
      border-radius: 2rem;
      background: #FF6633;
    }

    <div
      class="c0"
    />
  `)
})

test('should render a ProgressLinear spreading props in root element', () => {
  const { container } = render(<ProgressLinear id="bar" />)

  expect(container.firstChild).toHaveAttribute('id', 'bar')
})

test('should render a ProgressLinear using ref', () => {
  const ref = createRef<HTMLDivElement>()
  const { container } = render(<ProgressLinear ref={ref} />)

  expect(ref.current).toBe(container.firstChild)
})

test('should render a ProgressLinear using percentagem prop', () => {
  const { container } = render(<ProgressLinear percentage={70} />)

  expect(container.firstChild).toHaveStyleRule('width', '70%', {
    modifier: '::after',
  })
})

describe('ColorScheme Prop', () => {
  it.each(colorSchemes)('as %s', (colorSchemeName) => {
    const { container } = render(<ProgressLinear colorScheme={colorSchemeName} />)
    const colorScheme = defaultTheme.colorScheme[colorSchemeName]

    expect(container.firstChild).toHaveStyleRule('background', `${colorScheme['500']}`, {
      modifier: '::after',
    })
  })
})

describe('Component Style', () => {
  it('extends style from theme', () => {
    const customTheme = buildThemeWithComponents('ProgressLinear', 'base', {
      padding: '100px',
    })

    const { container } = render(<ProgressLinear />, {
      theme: customTheme,
    })

    expect(container.firstChild).toHaveStyleRule('padding', '100px')
  })

  it('overwrite style from theme', () => {
    const customTheme = buildThemeWithComponents('ProgressLinear', 'overwrite', {
      backgroundColor: 'mediumpurple',
    })

    const { container } = render(<ProgressLinear />, {
      theme: customTheme,
    })

    expect(container.firstChild).toHaveStyleRule('background-color', 'mediumpurple')
  })

  it('overwrite style with sx prop', () => {
    const { container } = render(<ProgressLinear sx={{ backgroundColor: 'mediumpurple' }} />)

    expect(container.firstChild).toHaveStyleRule('background-color', 'mediumpurple')
  })
})
