import { readFile } from 'fs/promises'
import open from 'open'
import { getUrl } from './url.js'

async function start() {
  try {
    const config = JSON.parse(await readFile('./preview.json', { encoding: 'utf-8' }))
    const url = `${getUrl()}?extensionTest=${encodeURIComponent(JSON.stringify(config))}`
    await open(url)
    // eslint-disable-next-line no-undef
    process.exit(0)
  } catch (error) {
    // eslint-disable-next-line no-console
    console.error(error)
    // eslint-disable-next-line no-undef
    process.exit(1)
  }
}

start()
