const prd = 'https://app.stackspot.com'
const stg = 'https://app.stg.stackspot.com'
const dev = 'https://app.dev.stackspot.com'
const local = 'http://localhost:3000'

export function getUrl() {
  // eslint-disable-next-line no-undef
  const args = process.argv.slice(2)
  if (args.includes('--stg')) return stg
  if (args.includes('--dev')) return dev
  if (args.includes('--local')) return local
  return prd
}
