import { createRequestMessage } from '@stack-spot/portal-extension-commons'
import { messageHandler } from './MessageHandler'

export async function getExtensionSlug(): Promise<string> {
  const response = await messageHandler.sendRequest(createRequestMessage('getExtensionSlug'))
  return response.result
}

export async function getExtensionVersion(): Promise<string | undefined> {
  const response = await messageHandler.sendRequest(createRequestMessage('getExtensionVersion'))
  return response.result
}
