import { NetworkConfig } from '@stack-spot/portal-extension-commons'

let config: NetworkConfig | undefined
let resolve: () => void
const promise = new Promise<void>((r) => {
  resolve = r
})

export function networkConfigReady() {
  return promise
}

export function getNetworkConfig() {
  return config!
}

export function setNetworkConfig(clients: NetworkConfig) {
  config = clients
  resolve()
}
