import { UserData, createRequestMessage } from '@stack-spot/portal-extension-commons'
import { messageHandler } from './MessageHandler'

export async function getUserData(): Promise<UserData> {
  const response = await messageHandler.sendRequest(createRequestMessage('getUserData'))
  return response.result
}

export async function getCurrentPage() {
  const response = await messageHandler.sendRequest(createRequestMessage('getCurrentPage'))
  return response.result
}
