import { shouldShowNpsModal } from './hook'

export const checkVisibility = (event: StorageEvent, fnUpdateState: React.Dispatch<React.SetStateAction<boolean>>) => {
  if (event.key === 'RATED_US_IN') {
    const shouldShow = shouldShowNpsModal()
    if (!shouldShow) {
      fnUpdateState(false)
    }
  }
}
